package connect.four

fun main() {

    train()

}

fun train() {
    for (i in 27 downTo 4) {
        for (k in 1..5) Minimax.Storage.seedByMovesPlayed<Move>(500, i)
    }
}

fun playGame() {
    var game = ConnectFour()

    while (!game.isGameOver()) {
        println(game)
        print("Enter move: ")

        val move = Move(readLine()!!.toInt())
        game = game.move(move)

        if (game.isGameOver()) break
        game = game.bestMove()
    }

    println(game)
}